import React, { useState, useEffect } from 'react';

function Employees() {

    const [employees, setEmployees] = useState([]);

    const getEmployees = () => {
        fetch('/employees')
            .then(response => {
                return response.json()
            })
            .then(data => {
                setEmployees(data)
            })
            .catch(error => console.log(error.message));
    }

    useEffect(() => {
        getEmployees()
    }, []);

    return (
        <div>
            <h2>Employees</h2>

            <table>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                        <th>Value</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((employee) => (
                        <tr>
                            <td>{employee.id}</td>
                            <td>
                                <input value={employee.name} />
                            </td>
                            <td>{employee.value}</td>
                            <td>
                                <button onClick={() => edit(employee)}>Edit</button>
                                <button onClick={() => remove(employee.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                    <tr>
                        <td>
                            <input />
                        </td>
                        <td>
                            <input />
                        </td>
                        <td>
                        <button>Add</button>
                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
    );
}

export default Employees;
